			define('LIB_PATH',			APP_PATH.'lib/');				//SET THE LIBRARY PATH
		}

		//CHECK FOR POST REQUESTS AND ROUTE ACCORDINGLY
		private function route(){

			//IF WE ARENT POSTING DATA SHOW THE INSTALLER TEMPLATE
			if(empty($_POST)){
				$this->installer_template_welcome();
			}

			//DATA WAS POSTED SO VALIDATE IT
			elseif($_POST['start_install']){
				$this->validate_install();
			}			
		}

		//VALIDATE THE INSTALL REQUEST
		private function validate_install(){

			//STORE THE POST DATA
			$data = $_POST;

			//ARE WE CONNECTING TO A DATABASE?
			if($_POST['use_db'] == 'on'){

				//TRY TO CONNECT
				$link =  mysql_connect($_POST['db_host'], $_POST['db_user'], $_POST['db_password']);
				
				//CONNECT WAS NOT SUCCESSFULL
				if(!$link){
					$data['errors'][] = 'Unable to connect to database with provided credentials'; 
				}

				//CONNECT WAS SUCCESSFULL
				else{

					//SELECT THE DATABASE
					$select = 	mysql_select_db($_POST['db_name'], $link);

					//DATABASE DOES NOT EXIST
					if(!$link){
						$data['errors'][] = 'Connected to server but could not find database by the name of '.$_POST['db_name']; 
					}
				}
			}

			//WHEN ERRORS SEND BACK TO INSTALLER WITH ERRORS
			if($data['errors']){
				$this->installer_template_welcome($data);
				exit;
			}

			//START THE INSTALLER
			else{

				//CREATE THE FILES
				$this->build_files();				
			}		
		}

//----------------------------------------------// INSTALLER HELPER METHODS // ---------------------------------------------//

		
		//CREATE A FILE
		private function create_file($filename, $content){
			
			//MAKE SURE DIRECTORY EXISTS
			$this->force_dir(dirname($filename));

			//OPEN THE FILE
			$handle = fopen($filename, "w+");

			//OVERWRITE THE FILE WITH THE CONTENT
			fwrite($handle, $content);

			//CLOSE THE FILE
			fclose($handle);
		}
		

		private function create_tables(){


			if($_POST['use_db'] == 'on'){

				//DATABASE CREDENTIALS
				$servername = $_POST['db_host'];
				$username 	= $_POST['db_user'];
				$password 	= $_POST['db_password'];
				$dbname 	= $_POST['db_name'];
				$conn 		= new mysqli($servername, $username, $password, $dbname);


				$user_password = md5('password');
				$tables = array(
					'user'	=>
						array(
							'create_query' =>  "CREATE TABLE IF NOT EXISTS `user` (user_id INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY, user_first_name VARCHAR(30) NOT NULL, user_last_name VARCHAR(30) NOT NULL, user_email VARCHAR(50), user_password VARCHAR(255))",
							'insert_query' => "INSERT INTO `user` VALUES (null, 'Admin', 'User', 'example@domain.com', '{$user_password}')",
						),					
				);

				foreach($tables as $table){					
					if(isset($table['create_query'])){
						if($conn->query($table['create_query'])){
							if(isset($table['insert_query'])){
								$conn->query($table['insert_query']);
							}
						}
						
					}					
				}
			}
		}

		//CREATE A DIRECTORY RECURSIVELY IF NECESSARY
		private function force_dir($dir){
			
			//IF THE DIRECTORY IS NOT FOUND
			if(!file_exists($dir)){

				//MAKE THE DIRECTORY
				mkdir($dir, 0777, true);
			}
		}

		private function install_files(){

			//GET THE FILES DATA
			$file_types = $this->files();

			//CYCLE THE FILE TYPES
			foreach($file_types as $type => $files){

				//CYCLE THE FILES IN THIS TYPE
				foreach($files as $path => $file){
					
					//DECODE THE FILES AND CREATE THEM
					$this->create_file($path, base64_decode($file));
				}
			}
		}

		//INSTALL AN INDIVIDUAL PLUGIN
		private function install_plugin($data){

			//GET THE PLUGIN POST ACTIONS
			$actions = $this->plugin_action();

			//DECODE PLUGIN DATA
			$data = unserialize(base64_decode($data));
			
			//GET THE NAME FROM THE PLUGIN DATA ARRAY
			$name = $data['plugin_name'];

			//REMOVE THE NAME FROM THE DATA
			unset($data['plugin_name']);
			unset($data['plugin_description']);

			//THE REST OF THE PLUGIN ARRAY SHOULD BE $path => base64_encoded_content
			//CYCLE THE CONTENT
			foreach($data as $path => $content){
				
				//DECODE THE CONTENT
				$content = base64_decode($content);
				
				//PLUGIN PATH = /plugin/
				//$name = PLUGIN_NAME
				//$path = /dir/subdir/file
				$path = PLUGIN_PATH.$name.$path;
				
				//CREATE THE FILE
				$this->create_file($path, $content);

				if(isset($actions[$name])){
					$method = $actions[$name];
					if(method_exists($this, $method)){
						$this->$method();
					}
				}
			}
		}

		//INSTALL AN INDIVIDUAL PLUGIN
		private function install_lib($data){

			//DECODE PLUGIN DATA
			$data = unserialize(base64_decode($data));
			
			//GET THE NAME FROM THE PLUGIN DATA ARRAY
			$name = $data['lib_name'];

			//REMOVE THE NAME FROM THE DATA
			unset($data['lib_name']);

			//THE REST OF THE PLUGIN ARRAY SHOULD BE $path => base64_encoded_content
			//CYCLE THE CONTENT
			foreach($data as $path => $content){
				
				//DECODE THE CONTENT
				$content = base64_decode($content);
				
				//LIB PATH = /lib/
				//$name = LIB_NAME
				//$path = /dir/subdir/file
				$path = LIB_PATH.$name.$path;
				
				//CREATE THE FILE
				$this->create_file($path, $content);
			}
		}



		//INSTALL ALL SELECTED PLUGINS
		private function install_plugins(){		

			//PLUGINS WERE SELECTED
			if($_POST['plugin'] && !empty($_POST['plugin'])){

				//GRAB AVAILABLE PLUGINS
				$plugins = $this->plugins();

				//CYCLE POSTED PLUGINS
				foreach($_POST['plugin'] as $plugin => $value){

					//MAKE SURE PLUGIN WAS SELECTED
					if($value == 'on'){

						//IF THE PLUGIN EXISTS
						if(isset($plugins[$plugin])){	

							//GRAB THE PLUGIN DATA
							$data = $plugins[$plugin];

							//SEND RAW PLUGIN DATA TO INSTALLER METHOD				
							$this->install_plugin($data);
						}
					}
				}
			}
		}

		//INSTALL SELECTED LIBRARIES
		private function install_libs(){
			
			//LIBRARIES WERE SELECTED
			if($_POST['lib'] && !empty($_POST['lib'])){
				
				//GRAB AVAILABLE LIBRARIES
				$libraries = $this->libs();

				//CYCLE THE SELECTED LIBRARIES
				foreach($_POST['lib'] as $library){

					//IF THE SELECTED LIBRARY IS AVAILABLE
					if(isset($libraries[$library])){

						//GRAB THE LIBRARY DATA
						$data = $libraries[$library];

						//SEND RAW PLUGIN DATA TO INSTALLER METHOD				
						$this->install_lib($data);
					}
				}
			}
		}
		

